srcDoc.css:
body {
  font-family: sans-serif;
  background-color: var(--appsmith-theme-backgroundColor);
  color: var(--appsmith-theme-primaryColor);
  size: 16px;
}

.chat-widget {
  display: flex;
  flex-direction: column;
  height: 100%;
  background-color: #2e2e2e;
}

.messages-container {
  flex: 1;
  position: absolute;
  top: 0;
  overflow-y: auto;
  padding: 16px;
  width: 95%;
  height: 100%;
}

.content {
  margin: 4px 12px;
  border: solid 1px grey;
  border-radius: 4px;
  padding: 4px 12px;
}

.user-message {
  color: black;
}

srcDoc.js:
const messagesContainer = document.getElementById('messages-container');
function renderMessage(message, isUser) {
  const messageElement = document.createElement('div');
  messageElement.className = 'message ' + (isUser ? 'user-message' : '');
  messageElement.innerHTML = message || '';
  messagesContainer.appendChild(messageElement);
}
function updateMessages() {
  const chatHistory = appsmith.model.chatHistory || [];
  if (!chatHistory[0]?.content) return;
  messagesContainer.innerHTML = '';
  chatHistory.forEach(msg => renderMessage(msg.content, msg.role === 'user'));
}
appsmith.onReady(() => {
  appsmith.onModelChange(() => {
    updateMessages();
  });
});

